import 'bootstrap/dist/css/bootstrap.css'
import { useState } from 'react';
import  { useForm} from 'react-hook-form';

import './App.css';
import './index.css';


function App() {
    let countries=["india","australia","pakistan","srilanka","bangladesh","america","canada","france"];
    let [randomNum,setRandom]=useState(0);
    let st="";
    for(let i ;i<x.length;i++){
      st+="*";
    }
    let [len,setLen]=useState("*****");
    let x=countries[randomNum];
    let newCountry=()=>{
        setRandom(Math.floor(Math.random()*8));
        setLen(x.replace(/./g,"*"));
      }

    console.log(x);
    console.log(len);



    let [alpha,setAlpha]=useState(len);
    let [num,setNum]=useState(5);
    let [flag,setFlag]=useState(-1);
    const {register,handleSubmit,formState:{errors}}=useForm();
     let onFormsubmit=(obj)=>{

      if((x).indexOf(obj.abc)>-1){
        for(let i=0;i<x.length;i++){
          if(x[i]==obj.abc){
            alpha=alpha.substring(0,i)+obj.abc+alpha.substring(i+1);
          }
          }
          setFlag(1);
          setAlpha(alpha);
        }

      else{
        setNum(num-1);
        setFlag(0);
         }
      }



 return(
  <div>
    <div className='heading'>
      <h1 className="text-center m-4" style={{color:'darkslategrey'}}>GUESS THE COUNTRY NAME</h1>
      </div>
      <form onSubmit={handleSubmit(onFormsubmit)}>
        <div className=' row m-5'>
          <div className='col-sm-3'>
          <label className='text-center  display-6 ms-4  mt-2 text-primary'><b>Enter a Letter</b></label>
          </div>
          <div className='col-sm-9'>
          <input type="text" className='form-control w-75 ms-4 mt-3  ' id="abc" {...register("abc",{required:true})} placeholder='Enter a single alphabet'/>
          {
            errors.abc?.type=='required' && <p className='text-danger  ms-5'>*Required</p>
          }
          </div>
        </div>

          <div className='button m-4'>
          <button className='btn btn-success  col-sm-3' type='submit'>Check For Alphabet</button>
          <button className='btn btn-success  col-sm-3'type='button' onClick={newCountry} style={{float:'right'}}>New Country</button>
          </div>
          <div className='stars text-center mt-3'>
          <h3 className='text-center mt-5 display-3 text-dark'>{alpha}</h3>
          </div>
          {
            alpha.localeCompare(x)==0?<h2 className='text-success text-center'>Congrats You Have Won The Game!!!! :)</h2>:( flag==1 && num>0?<h3 className='text-center'>Yess!! You have found a Letter</h3>:(flag==0 && num>=1?<h3 className='text-center m-5'>Oops! You Have Lost A chance</h3>:<p></p>))
          }
          {
            num<1?<div>
              <h3 className='text-danger text-center'> Sorry!! You have Lost the Game</h3 >
              <h4 className='text-center m-4'>The Answer is {x}</h4>
              </div>:<h2 className=' chances text-center mt-3 text-dark'>You still have {num} chances</h2>
          }

      </form>





  </div>
 )
}

export default App;
