import 'bootstrap/dist/css/bootstrap.css'
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import Random from './components/Random';
import './App.css';
import './index.css';
import Collapse from './components/Collapse';



function App() {

  let [x, setx] = useState(Random());

  let y = "";
  for (let i = 0; i < x.length; i++) {
    y += "*";

  }
  let [alpha, setAlpha] = useState(y);
  let [num, setNum] = useState(5);
  let [flag, setFlag] = useState(-1);
  const { register, handleSubmit, formState: { errors } } = useForm();
  let onFormsubmit = (obj) => {

    if ((x).indexOf(obj.abc) > -1) {
      for (let i = 0; i < x.length; i++) {
        if (x[i] == obj.abc) {
          alpha = alpha.substring(0, i) + obj.abc + alpha.substring(i + 1);
        }
      }
      setFlag(1);
      setAlpha(alpha);
    }

    else {
      setNum(num - 1);
      setFlag(0);
    }
  }



  return (
    <div>
      <div className='heading'>
        <h1 className="text-center m-4" style={{ color: 'darkslategrey' }}>GUESS THE COUNTRY NAME</h1>
      </div>
      <Collapse/>
      <form onSubmit={handleSubmit(onFormsubmit)}>
        <div className=' row m-5'>
          <div className='col-sm-3'>
            <label className='text-center  display-6 ms-4  mt-2 text-primary'><b>Enter a Letter</b></label>
          </div>
          <div className='col-sm-9'>
            <input type="text" className='form-control w-75 ms-4 mt-3  ' id="abc" {...register("abc", { required: true })} placeholder='Enter a lowercase alphabet' />
            {
              errors.abc?.type == 'required' && <p className='text-danger  ms-5'>*Required</p>
            }
          </div>
        </div>

        <div className='button ms-4'>
          <button className='btn btn-success ms-5  col-sm-3' type='submit'>Check For Alphabet</button>
        </div>
        <div className='stars  mt-3'>
          <h3 className='text-center mt-4 display-3 text-dark'>{alpha}</h3>
        </div>
        {
          alpha.localeCompare(x) == 0 && num>1 ?<div><h2 className='text-success text-center'>Congrats You Have Won The Game!!!! :)</h2> <h4 className='text-secondary text-center'>Refresh the page to Play again!!</h4></div> : (flag == 1 && num > 0 ? <h3 className=' yes text-center  m-4 '>Yess!! You have found a Letter</h3> : (flag == 0 && num >= 1 ? <h3 className='text-center m-5 text-danger'>Oops! You Have Lost A chance</h3> : <p></p>))
        }
        {
          num < 1 ? <div>
            <h3 className=' sorry text-danger text-center'> Sorry!! You have Lost the Game</h3 >
            <h4 className='text-center m-4'>The Answer is {x}</h4>
            <h4 className='text-secondary text-center'>Refresh the page to Play again!!</h4>
          </div> : <h2 className=' chances text-center  text-dark'>You still have {num} chances</h2>
        }

      </form>




    </div>
  )
}

export default App;
