import 'bootstrap/dist/css/bootstrap.css'
import { useState } from 'react';
import  { useForm} from 'react-hook-form';
import Useeffect from './components/Useeffect';
import './App.css';

function App() {
    let x="russia";
    let len="";
    let chances=5;
    for(let i=0;i<x.length;i++){
      len+="*";
    }
    let [alpha,setAlpha]=useState(len);
    let [num,setNum]=useState(5);
    let [flag,setFlag]=useState(-1);
    const {register,handleSubmit,formState:{errors}}=useForm();
     let onFormsubmit=(obj)=>{

      if((x).indexOf(obj.abc)>-1){
        for(let i=0;i<x.length;i++){
          if(x[i]==obj.abc){
            alpha=alpha.substring(0,i)+obj.abc+alpha.substring(i+1);
          }
          }
          setFlag(1);
          setAlpha(alpha);
        }

      else{
        console.log("no");
        setNum(num-1);
        setFlag(0);
         }
      }



 return(
  <div>
      <h1 className="text-center m-3">GUESS THE COUNTRY</h1>
      <form onSubmit={handleSubmit(onFormsubmit)}>
        <div className=' row m-5'>
          <div className='col-sm-3'>
          <label className='text-center  display-6 ms-5'>Enter a Letter</label>
          </div>
          <div className='col-sm-9'>
          <input type="text" className='form-control w-75  ' id="abc" {...register("abc",{required:true})}/>
          {
            errors.abc?.type=='required' && <p className='text-danger fw-bold ms-2'>*Required</p>
          }
          </div>
        </div>
          <div className='ms-5'>
          <button className='btn btn-success ms-5 col-sm-3'>Check For The Alphabet</button>
          </div>
          <h3 className='text-center m-5 display-5'>{alpha}</h3>
          {
            flag==1?<h3 className='text-center'>Yess!!</h3>:(flag==0?<h3 className='text-center m-5'>Oops! You Have Lost A chance</h3>:<p></p>)
          }
          {
            num<1?<h2 className='text-danger text-center'> Sorry!! You have Lost the Game :(</h2>:<h2 className='text-center mt-5 text-warning'>You still have {num} chances</h2>
          }

      </form>





  </div>
 )
}

export default App;
