import 'bootstrap/dist/css/bootstrap.css'
import { useState } from 'react';
import  { useForm} from 'react-hook-form';
import Useeffect from './components/Useeffect';
import './App.css';

function App() {
    let x="sweden";
    let len="";
    let chances=5;
    for(let i=0;i<x.length;i++){
      len+="*";
    }
    let [alpha,setAlpha]=useState(len);
    const {register,handleSubmit,formState:{errors}}=useForm();
     let onFormsubmit=(obj)=>{

      if((x).indexOf(obj.abc)>-1){
        for(let i=0;i<x.length;i++){
          if(x[i]==obj.abc){
            alpha=alpha.substring(0,i)+obj.abc+alpha.substring(i+1);
          }
          }
          setAlpha(alpha);
        }

      else{
        console.log("no")
        chances=chances-1;
      }
      }



 return(
  <div>
      <h1 className="text-center m-3">GUESS THE COUNTRY</h1>
      <form onSubmit={handleSubmit(onFormsubmit)}>
        <div>
          <label className='text-center m-5'>Enter a Letter</label>
          <input type="text" className='' id="abc" {...register("abc")}/>
        </div>
          <button className='btn btn-success ms-5'>Check</button>
          <h3 className='text-center'>{alpha}</h3>
          <h2 className='text-center'>You still have {chances} chances</h2>
      </form>





  </div>
 )
}

export default App;
