import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function App() {
  const [height, setHeight] = useState(0);
  const [mass, setMass] = useState(0);
  const [bmi, setBmi] = useState(0);

  const calculate = (e) => {
    e.preventDefault();
    const formValid = +height > 0 && +mass > 0;
    if (!formValid) {
      return;
    }
    const bmi = +mass / (+height) ** 2;
    setBmi(bmi);
  };

  return (
    <div className="">
      <div className="justify-content-center m-5">
      <h1 className="text-center">BMI CALCULATOR</h1>
      </div>
      <form onSubmit={calculate} className="">
        <div className="mb-3 container">
          <label className="form-label"><h5>Height in meters</h5></label>
          <input value={height} className="form-control" onChange={(e) => setHeight(e.target.value)} />
        </div>

        <div>
          <label>mass in kg</label>
          <input value={mass} onChange={(e) => setMass(e.target.value)} />
        </div>

        <button type="submit">calculate</button>
      </form>
      <p>bmi: {bmi}</p>
    </div>
  );
}
